  "languages": ["Terran"],
  "skills": ["Athletics +28", "Deception +27 (+31 to imitate stone)", "Survival +25"],
  "abilities": ["Str +8", " Dex +4", " Con +5", " Int +2", " Wis +5", " Cha +5"],
