  "abilities": ["Str +6", " Dex +4", " Con +4", " Int +1", " Wis +4", " Cha +3"],
  "ac": "of 19, Hardness 10, and 40 Hit Points",
  "saves": ["Fort +16", " Ref +12", " Will +14", " +1 status to all saves vs. magic"],
